import React, { useState, useEffect } from 'react';
import axios from 'axios';


import './Form.css';

const Form = () => {
  const [inputs, setInputs] = useState({ name: '', email: '', subject: '', description: '' });
  const checkInputs = () => {
    setInputs(inputs || "");
  };
  // const [currentInput, setCurrentInputs] = useState(null);
  const handleChange = e => {
    const { name, value } = e.target;
    setInputs(prev => ({ ...prev, [name]: value }));
  };
  const handleSubmit = e => {
    e.preventDefault();
    if (inputs.length) {
      // addItem({ id: currentContact?.id || "", name, surName, phone });
      // setCurrentInputs;
      setInputs("");
      //destructure from inputs 
      const { name, email, subject, description } = inputs;
      axios.post('/sendtome', {
        //make an object to be handled from req.body on the backend. 
        email,
        name,
        subject,
        //change the name to represent text on the backend.
        text: description
      });
    }
    else
      alert("Please fill the data");

  };
  useEffect(() => {
    checkInputs();
  });
  return (
    <div className="form__section">
      <div className="container">
        <h3 id="form">
          Zkontaktuj z nami !
        </h3>
        <form
          className="contact_form"
          onSubmit={handleSubmit}
        >
          <input
            type="text"
            id="name"
            name="name"
            placeholder="ImiÄ™.."
            value={inputs.name}
            onChange={handleChange}
          />

          <input
            type="email"
            id="email"
            name="email"
            placeholder="Email.."
            value={inputs.email}
            onChange={handleChange}
          />

          <input
            id="subject"
            name="subject"
            placeholder="Temat.."
            value={inputs.subject}
            onChange={handleChange}
          />

          <textarea
            id="description"
            name="description"
            placeholder="Napisz nam.."
            value={inputs.description}
            onChange={handleChange}
          >
          </textarea>

          <button
            className="button"
            type="submit"
            value="Submit"
          >
            submit
          </button>
        </form>
      </div>
    </div>
  );

};
export default Form;
